<?phpnamespace Controllers;use Managers\LoginManager;class LoginController{    public function loginVue()    {        require_once 'Vues/LoginVue.php';    }    public function login()    {        session_start();        $login = new LoginManager();        if ($login->login() === true && $_SESSION['CodeName']) {            header('Location: ?action=Profil');        }else {            header('Location: ?action=Login');        }    }    public function logout()    {        session_start();        session_destroy();        header('Location: ?action=Login');    }}