<?phpnamespace Controllers;use Models\LoginModel;class LoginController{    public function loginVue()    {        echo require_once 'Vues/LoginVue.php';    }    public function login()    {        $loginModel = new LoginModel();        $profilVue = new ProfilController();        if ($loginModel->login() === true) {            $profilVue->profilVue();        }elseif ($loginModel->login() === false) {            $_GET['action'] = 'Login';            echo require_once 'Vues/LoginVue.php';        }else {            echo 'erreur';        }    }}