<?phpnamespace Controllers;use Models\LoginModel;class LoginController{    public function loginVue()    {        require_once 'Vues/LoginVue.php';    }    public function login()    {        $login = new LoginModel();        if ($login->login() === true) {            header('Location: ?action=Profil');        }else {            header('Location: ?action=Login');        }    }    public function logout()    {        session_start();        session_destroy();        header('Location: ?action=Login');    }}