<?phpnamespace Models;use PDO;use PDOException;class FormModel{    public function createForm(MissionsModel $mission)    {        session_start();        function validDatas($data)        {            // Enlève espace inutile            $data = trim($data);            // Supprime les antislashs            $data = stripcslashes($data);            // Echappe caractères type < >            $data = htmlspecialchars($data);            return $data;        }        // Récupérer les données du form pour les stocker dans un tableau        // Renvoyer ses données vers le model/manager correspondant et les attribuer        if (isset($_POST['submit'])) {                $title = validDatas($_POST['title']);                $description = validDatas($_POST['description']);                $country = validDatas($_POST['country']);                $codeName = validDatas($_POST['codeName']);                $agent = validDatas($_POST['agent']);                $target = validDatas($_POST['target']);                $safehouse = validDatas($_POST['safehouse']);                $type = validDatas($_POST['type']);                $state = validDatas($_POST['state']);                $competence = validDatas($_POST['competence']);                $startDate = validDatas($_POST['startDate']);                $endDate = validDatas($_POST['endDate']);            try {                $pdo = new PDO('mysql:dbname=intelligence_agency;host=localhost', 'root', 'root');                $pdo->prepare("INSERT INTO     intelligence_agency.Missions    (title, description, country, codeName, agent, target, safeHouse, type, state, competence, startDate, endDate) VALUES     ('$title', '$description', '$country', '$codeName', '$agent', '$target', '$safehouse', '$type', '$state', '$competence', '$startDate', '$endDate')");            } catch (PDOException $e) {                echo $e;            }        }else {            echo 'Une erreur lors de la saisiedes infos est survenue';        }    }}