<?phpnamespace Vues\Login;use Vues\RenderGlobalVue;class LoginVue extends RenderGlobalVue{    public function render()    {        $content = '            <div class="container-connexion">            <form action="?action=Profil" method="POST" enctype="multipart/form-data">            <h1>Page de connexion</h1>                   <div>                    <label for="CodeName">Nom de code</label>                </div>                             <input type="text" name="CodeName" placeholder="Entrez votre nom de code">                <div>                    <label for="password">Mot de passe</label>                                </div>                     <input type="password" name="password" placeholder="Entrez votre mot de passe" required>                   <div class="btn">                    <input type="submit" id="submit" name="submit"><!-- value="LOGIN"-->                </div></form></div>';        $this->print($content);    }}