<?phpuse Controllers\DetailMissions;use Controllers\ListMissions;use Controllers\LoginController;use Controllers\HomeController;use Controllers\MissionsController;ini_set('display_errors', 1);ini_set('display_startup_errors', 1);error_reporting(E_ALL);require_once 'Autoloader.php';Autoloader::register();$homeController = new HomeController();$loginController = new LoginController();$detailMission = new DetailMissions();$listMission = new ListMissions();if (isset($_GET['action'])) {    switch ($_GET['action']){        case 'List':            $listMission->ListMissions();            break;        case 'Detail':            $detailMission->DetailMissionVue();            break;        case 'Login':            $loginController->loginVue();            break;    }}else {    echo require_once 'Vues/HomeVue.php';}