<?phpuse Controllers\AdminsController;use Controllers\LoginController;use Controllers\HomeController;use Controllers\MissionsController;ini_set('display_errors', 1);ini_set('display_startup_errors', 1);error_reporting(E_ALL);require_once 'Autoloader.php';Autoloader::register();$missionController = new MissionsController();$homeController = new HomeController();$loginController = new LoginController();$adminController = new AdminsController();if (isset($_GET['action'])) {    switch ($_GET['action']){        case 'List':            $missionController->ListMissions();            break;        case 'Mission':            $missionController->getMission();            break;        case 'Login':            $loginController->login();            break;        case 'Profil':            $adminController->profil();            break;        default:            $missionController->notFound('erreur 404');            break;    }}else {    $homeController->home();}