<?phpuse Controllers\FormController;use Controllers\LoginController;use Controllers\MissionController;use Controllers\ProfilController;ini_set('display_errors', 1);ini_set('display_startup_errors', 1);error_reporting(E_ALL);require_once 'Autoloader.php';Autoloader::register();$profilController = new ProfilController();$loginController = new LoginController();$formController = new FormController();$missionController = new MissionController();if (isset($_GET['action'])) {    switch ($_GET['action']){        case 'List':            $missionController->ListMissionsVue();            break;        case 'Detail':            $missionController->DetailMissionVue();            break;        case 'Login':            $loginController->loginVue();            break;        case 'Logged':            $loginController->login();            break;        case 'Logout':            $loginController->logout();            break;        case 'Profil':            $profilController->profilVue();            break;        case 'Form':            $formController->formVue();            break;        case 'Create':            $formController->formCreate();            break;    }}else {    echo require_once 'Vues/HomeVue.php';}